#include "libRosalind.h"
#define LIMIT 1000
void REVC(void){
	time_t tInit=clock();
	char *s="";
	char resp[LIMIT]={'\0'};
	int len=strlen(s)-1;
	for(int i=len;i>=0;i--){
		switch(s[i]){
			case 'T':
				resp[len-i]='A';
				break;
			case 'A':
				resp[len-i]='T';
				break;
			case 'C':
				resp[len-i]='G';
				break;
			case 'G':
				resp[len-i]='C';
				break;
		}
	}
	time_t tEnd=clock();
	printf("Problem REVC - Result: %s. Elapsed Time: %.6f\n", resp,(double) (tEnd-tInit)/CLOCKS_PER_SEC);
	return;
}
